import React, { useState, useEffect, useCallback } from 'react';
import { fetchConversations } from '../services/api';

const ConversationList = ({ folderId = null }) => {
    const [conversations, setConversations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [page, setPage] = useState(1);
    const [hasMore, setHasMore] = useState(true);

    const loadConversations = useCallback(async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await fetchConversations(page, folderId);
            setConversations(prevConversations => [...prevConversations, ...response.data.results]);
            setHasMore(response.data.next !== null);
            if (response.data.next) {
                setPage(prevPage => prevPage + 1); // Increment page only if there is a next page
            }
        } catch (err) {
            console.error("Error fetching conversations:", err);
            setError("Failed to load conversations. Please try again.");
        } finally {
            setLoading(false);
        }
    }, [page, folderId]);
    

    useEffect(() => {
        loadConversations();
    }, [loadConversations]);

    // ... rest of the component remains the same ...
};

export default ConversationList;
