import React, { useState, useEffect } from 'react';
import { fetchGroups } from '../services/api'; // Adjust if you have a different API method
import { Link } from 'react-router-dom';

const GroupList = () => {
    const [groups, setGroups] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchGroups() // Adjust to your actual API call
            .then(response => {
                console.log("Groups:", response.data);
                setGroups(response.data.results); // Assuming 'results' contains groups
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching groups:", error);
                setError('Failed to load groups.');
                setLoading(false);
            });
    }, []);

    if (loading) return <div>Loading groups...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <h2>Groups</h2>
            {groups.length === 0 ? (
                <p>No groups found.</p>
            ) : (
                <ul>
                    {groups.map(group => (
                        <li key={group.id}>
                            {group.name} 
                            <Link to={`/groups/edit/${group.id}`}>Edit</Link> {/* Add Edit link */}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default GroupList;
