import React, { useState, useEffect } from 'react';
import FolderList from './services/FolderList';
import ConversationList from './services/ConversationList';
import SearchBar from './services/SearchBar';
import Login from './services/Login';
import GroupList from './services/GroupList';
import EditGroupComponent from './services/EditGroupComponent';
import { Routes, Route } from 'react-router-dom';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('auth_token');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
  };

  if (!isAuthenticated) {
    return <Login onLoginSuccess={handleLoginSuccess} />;
  }
  

  return (
    <Router>
      <div className="App">
        <SearchBar />
        <Switch>
          <Route exact path="/" component={GroupList} />
          <Route path="/groups/edit/:id" component={EditGroupComponent} />
        </Switch>
        <FolderList />
        <ConversationList folderId={null} />
      </div>
    </Router>
  );
}
export const fetchGroup = (id) => api.get(`groups/${id}/`);

export default App;
