import axios from 'axios';

const API_URL = 'http://localhost:8000/api/';

const api = axios.create({
  baseURL: API_URL,
  withCredentials: true,
});

api.interceptors.request.use(
  config => {
    const token = localStorage.getItem('auth_token');
    if (token) {
      config.headers['Authorization'] = `Token ${token}`;
    }
    return config;
  },
  error => Promise.reject(error)
);

api.interceptors.response.use(
  response => response,
  async error => {
    if (error.response && error.response.status === 401) {
      localStorage.removeItem('auth_token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const fetchConversations = () => {
    return axios.get('http://localhost:8000/api/conversations/', {
        headers: {
            'Authorization': `Token ${yourAuthToken}` // Replace `yourAuthToken` with the actual token
        }
    });
};

export const fetchFolders = () => api.get('folders/');
export const fetchGroups = () => {
    return axios.get('http://localhost:8000/api/groups/', {
        headers: {
            'Authorization': `Token ${'6a3de9f27f583cfe692f75a309b00273a7966cbd'}` // Replace `yourAuthToken` with the actual token
        }
    });
};
export const searchConversations = (query) => api.get('conversations/search/', { params: { q: query } });

export default api;
