import React, { useState, useEffect } from 'react';
import { fetchFolders } from '../services/api';

const FolderList = () => {
    const [folders, setFolders] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchFolders()
            .then(response => {
                console.log("Response data:", response.data); // Inspect the response format
                if (Array.isArray(response.data)) {
                    setFolders(response.data);
                } else {
                    throw new Error('Unexpected response format');
                }
                setIsLoading(false);
            })
            .catch(error => {
                console.error("Error fetching folders:", error);
                setError('Failed to fetch folders. Please try again later.');
                setIsLoading(false);
            });
    }, []);

    if (isLoading) return <div>Loading folders...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <h2>Folders</h2>
            {folders.length === 0 ? (
                <p>No folders found.</p>
            ) : (
                <ul>
                    {folders.map(folder => (
                        <li key={folder.id}>{folder.name}</li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default FolderList;
