import axios from 'axios';
import React, { useEffect, useState } from 'react';

const FolderList = () => {
    const [folders, setFolders] = useState([]);
    const [error, setError] = useState(null);

    const fetchFolders = () => {
        const token = localStorage.getItem('token'); // Retrieve the token from local storage or any other secure storage

        axios.get('http://localhost:8000/api/folders/', {
            headers: {
                'Authorization': `Bearer ${token}` // Replace `token` with the actual token
            }
        })
        .then(response => {
            setFolders(response.data);
        })
        .catch(error => {
            setError(error);
            console.error('Error fetching folders:', error);
        });
    };

    useEffect(() => {
        fetchFolders();
    }, []);

    return (
        <div>
            {error ? (
                <div>Error: {error.message}</div>
            ) : (
                <ul>
                    {folders.map(folder => (
                        <li key={folder.id}>{folder.name}</li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default FolderList;
