import React, { useState, useEffect, useCallback } from 'react';
import { fetchConversations } from '../services/api';  // Adjust the path if needed

const ConversationList = ({ folderName = null }) => {
    const [conversations, setConversations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [page, setPage] = useState(1);
    const [hasMore, setHasMore] = useState(true);

    const loadConversations = useCallback(async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await fetchConversations(page, folderName);
            setConversations(prevConversations => [...prevConversations, ...response.data.results]);
            setHasMore(response.data.next !== null);
            setPage(prevPage => prevPage + 1);
        } catch (err) {
            console.error("Error fetching conversations:", err);
            setError("Failed to load conversations. Please try again.");
        } finally {
            setLoading(false);
        }
    }, [page, folderName]);

    useEffect(() => {
        loadConversations();
    }, [loadConversations]);

    if (loading && conversations.length === 0) return <div>Loading conversations...</div>;
    
    if (error) return (
        <div>
            <h2>Error loading conversations</h2>
            <p>{error}</p>
            <button onClick={() => loadConversations()}>Retry</button>
        </div>
    );

    return (
        <div>
            <h2>Conversations</h2>
            <ul>
                {conversations.map(conversation => (
                    <li key={conversation.id}>{conversation.title}</li>
                ))}
            </ul>
            {hasMore && <button onClick={loadConversations}>Load More</button>}
            {loading && <div>Loading more...</div>}
        </div>
    );
};

export default ConversationList;
