import axios from 'axios';

const API_URL = 'http://127.0.0.1:8000/api/';

// Create an axios instance with default config
const api = axios.create({
  baseURL: API_URL,
  withCredentials: true, // If you need to send cookies with your requests
});

// Add a response interceptor for error handling
api.interceptors.response.use(
  response => response,
  error => {
    console.error("API Error:", error.response ? error.response.data : error.message);
    return Promise.reject(error);
  }
);

export const fetchFolders = () => api.get('folders/');
export const fetchConversations = () => api.get('conversations/');
export const searchConversations = (query) => api.get('conversations/search/', { params: { q: query } });
