import React, { useState, useEffect } from 'react';
import FolderList from './services/FolderList';
import ConversationList from './services/ConversationList';
import SearchBar from './services/SearchBar';
import Login from './services/Login';
import GroupList from './services/GroupList'; // Import the new component
import EditGroupComponent from './services/EditGroupComponent';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const Route = () => (
    <Router>
        <Switch>
            {/* Other routes */}
            <Route path="/groups/edit/:id" component={EditGroupComponent} />
        </Switch>
    </Router>
);


  useEffect(() => {
    const token = localStorage.getItem('auth_token');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
  };

  if (!isAuthenticated) {
    return <Login onLoginSuccess={handleLoginSuccess} />;
  }

  return (
    <div className="App">
      <SearchBar />
      <GroupList /> {/* Add the GroupList component */}
      <FolderList />
      {/* Pass folderId to ConversationList if needed */}
      <ConversationList folderId={null} />
    </div>
  );
}

export default App;
