import React, { useState, useEffect } from 'react';
import { fetchConversations } from '../services/api'; // Adjust if necessary
import { Link } from 'react-router-dom';

const ConversationList = () => {
    const [conversations, setConversations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchConversations() // Adjust to your actual API call
            .then(response => {
                console.log("Conversations:", response.data);
                setConversations(response.data.results); // Assuming 'results' contains conversations
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching conversations:", error);
                setError('Failed to load conversations.');
                setLoading(false);
            });
    }, []);

    if (loading) return <div>Loading conversations...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <h2>Conversations</h2>
            {conversations.length === 0 ? (
                <p>No conversations found.</p>
            ) : (
                <ul>
                    {conversations.map(conversation => (
                        <li key={conversation.id}>
                            {conversation.title}
                            <Link to={`/conversations/edit/${conversation.id}`}>Edit</Link>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ConversationList;
