import React, { useState, useEffect } from 'react';
import { fetchGroup, updateGroup } from '../services/api'; // Adjust import as needed
import { useParams, useNavigate  } from 'react-router-dom';

const EditGroupComponent = () => {
    const { id } = useParams(); // Extract group ID from URL params
    const navigate = useNavigate();
    
    const [group, setGroup] = useState(null);
    const [name, setName] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        const getGroup = async () => {
            try {
                const response = await fetchGroup(id);
                setGroup(response.data);
                setName(response.data.name);
            } catch (error) {
                setError('Failed to fetch group details.');
            }
        };
        getGroup();
    }, [id]);
    

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
          await updateGroup(id, { name });
          navigate('/groups');
        } catch (error) {
          if (error.response) {
            console.error("Response data:", error.response.data);
            console.error("Response status:", error.response.status);
            console.error("Response headers:", error.response.headers);
            
            if (error.response.status === 403) {
              setError('Permission denied. You may not have the right to edit this group.');
            } else {
              setError(`Failed to update group: ${error.response.data.detail || 'Unknown error'}`);
            }
          } else {
            setError('Failed to update group. Please check your network connection.');
          }
        }
      };

    if (!group) return <div>Loading...</div>;

    return (
        <div>
            <h2>Edit Group</h2>
            {error && <div>{error}</div>}
            <form onSubmit={handleSubmit}>
                <label>
                    Name:
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </label>
                <button type="submit">Update Group</button>
            </form>
        </div>
    );
};

export default EditGroupComponent;
