import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FolderList from './services/FolderList';
import ConversationList from './services/ConversationList';
import SearchBar from './services/SearchBar';
import Login from './services/Login';
import GroupList from './services/GroupList';
import EditGroupComponent from './services/EditGroupComponent';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

const HomePage = () => (
  <div>
    <h1>Welcome to the Home Page</h1>
    <SearchBar />
    <GroupList />
    <FolderList />
    <ConversationList folderId={null} />
  </div>
);

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('auth_token');
    if (token) {
      setIsAuthenticated(true);
      axios.defaults.headers.common['Authorization'] = `Token ${token}`;
    }
  }, []);

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    const token = localStorage.getItem('auth_token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Token ${token}`;
    }
  };

  if (!isAuthenticated) {
    return <Login onLoginSuccess={handleLoginSuccess} />;
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/groups/edit/:id" element={<EditGroupComponent />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
